changeSet.sql:
create table route (
    id bigint not null primary key,
    name text
);

create table busStop (
id bigint not null primary key,
x point,
y point,
route_ID int
);

create table bus (
id bigint not null primary key,
name text,
route_ID bigint
);

create table schedule (
id bigint not null primary key,
route_ID bigint,
busstop_ID bigint,
time time with time zone
);
